INSERT INTO `movie` VALUES (1746193482053484583, '猛禽小队与哈莉·奎茵的奇妙解放', 'upload/vod/20240113-1/a60a1fafd7e197ea45765d06c79b6e25.jpg', '据称这部电影并不只是小丑女的个人电影。DC漫画《猛禽小队》实际上讲述了小丑女和黑金丝雀、蝙蝠女、女猎手等人物组队打击犯罪的故事。但目前尚不清楚片中将会出现哪些角色。《大黄蜂》编剧克里斯蒂娜·霍森执笔剧', '玛格特·罗比,玛丽·伊丽莎白·温斯特德,伊万·麦克格雷格,黄阿丽', '2020-02-07', '美国', '动作,犯罪,冒险', '动作片', 'https://hnzy.bfvvs.com/play/Yer2MJwe/index.m3u8', 279, 248, 897, 5.8, 353, 0, 279);
