INSERT INTO `movie` VALUES (1746193482053484586, '猛虎嗅蔷薇', 'upload/vod/20240113-1/8413047194d1e57574652708fc5991bd.jpg', '小虎曾是孟家养子，但意外导致养父过世，便离开孟家，漂泊十年后，成了走私贩。他偶然救下采芹和采薇两姐妹，更与采芹一见钟情，可采芹正是仕龙的未婚妻。采芹既迷恋小虎，又想嫁给仕龙做上等人。采芹与仕龙成亲那日', '康磊,程瑶瑶,李熠霖', '2012-04-28', '中国大陆', '', '剧情片', 'https://hnzy.bfvvs.com/play/Rb46Y56e/index.m3u8', 40, 930, 430, 6.2, 950, 0, 40);
